INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('EBR', 'STOCK', 'Centrais Elétricas Brasileiras S.A. - Eletrobrás', 'NYQ', 'Utilities', 'Utilities - Renewable', 'A Eletrobras é uma empresa brasileira de energia, especializada na geração e transmissão de energia elétrica. As operações da Companhia são divididas em dois segmentos primários: Geração de Energia e Transmissão de Energia. O portfólio de energia da Eletrobras consiste principalmente em fontes de baixo carbono, com a maioria das hidrelétricas, complementadas pela energia eólica e solar. No segmento de Geração de Energia, a Eletrobras administra uma ampla gama de Usinas, incluindo hidrelétricas e parques eólicos. O segmento de Transmissão de Energia envolve supervisionar uma ampla rede de linhas de transmissão em todo o Brasil. A Companhia opera através de uma rede de subsidiárias regionais.', 'Rio de Janeiro, RJ, Brazil', 7710);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CMI', 'STOCK', 'Cummins Inc.', 'NYQ', 'Industrials', 'Specialty Industrial Machinery', 'A Cummins Inc. projeta, fabrica, distribui e faz manutenção em motores a diesel e gás natural e produtos de componentes relacionados a motores. Os segmentos da empresa incluem motores, distribuição, componentes e sistemas de energia. O segmento de motores fabrica e comercializa uma variedade de motores movidos a diesel e gás natural sob a marca Cummins, bem como certas marcas de clientes, para caminhões pesados e médios, ônibus, veículos recreativos (RV), automóveis leves e mercados agrícolas. O segmento de Distribuição consiste nas linhas de produtos, que atendem e / ou distribuem uma gama de produtos e serviços, incluindo peças, motores, geração de energia e serviços. O segmento de componentes fornece produtos, incluindo sistemas de pós-tratamento, turbocompressores, produtos de filtração e sistemas de combustível para aplicações comerciais de diesel. O segmento de componentes fornece produtos, incluindo sistemas de pós-tratamento, turbocompressores, produtos de filtração e sistemas ', 'Columbus, IN, United States', 69600);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CMCO', 'STOCK', 'Columbus McKinnon Corporation', 'NMS', 'Industrials', 'Farm & Heavy Construction Machinery', 'Columbus McKinnon Corporation is a designer, manufacturer and marketer of intelligent motion solutions for material handling. Its key products include hoists, crane components, precision conveyor systems, rigging tools, light rail workstations and digital power and motion control systems. The Company focuses on commercial and industrial applications that require the safety and quality provided by its design and engineering know-how. Its products include a wide variety of electric, air-powered, lever, and hand hoists, hoist trolleys, explosion-protected hoists, winches, and aluminum work stations; alloy and carbon steel chain; forged attachments, such as hooks, shackles, textile slings, clamps, and load binders; mechanical and electromechanical actuators and rotary unions; below-the-hook special purpose lifters; and power and motion control systems, among others. Its target market verticals include manufacturing, transportation, energy and utilities, aerospace, and others.', 'Charlotte, NC, United States', 3478);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CRNT', 'STOCK', 'Ceragon Networks Ltd.', 'NMS', 'Technology', 'Communication Equipment', 'Ceragon Networks Ltd. offers wireless backhaul solutions. The Company''s products include FibeAir IP-20 Platform, FibeAir IP-20 Assured Platform and Network Management. The Company provides its services to wireless service providers, public safety organizations, government agencies and utility companies, among others. Its solutions are deployed by over 460 service providers, as well as a range of private network owners, in over 130 countries. The Company''s FibeAir IP-20 platform offers flexibility in choosing all-outdoor, split-mount and all-indoor configurations to suit any deployment scenario. The FibeAir IP-20 platform includes product categories, such as shorthaul-access and shorthaul-aggregation. The FibeAir IP-20 Assured platform includes product categories, such as shorthaul-access, shorthaul-aggregation, small cells, longhaul and enterprise access. Its NetMaster is a Network Management System (NMS), which is designed for managing large-scale wireless backhaul networks.', 'Rosh Haayin, , Israel', 1056);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CIBR', 'STOCK', 'First Trust NASDAQ Cybersecurity ETF', 'NGM', NULL, NULL, 'The fund will normally invest at least 90% of its net assets (including investment borrowings) in the common stocks and depositary receipts that comprise the index. The index includes securities of companies classified as "cyber security" companies by CTA. The fund is non-diversified.', '', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BCAB', 'STOCK', 'BioAtla, Inc.', 'NGM', 'Healthcare', 'Biotechnology', 'BioAtla, Inc. is a clinical-stage biopharmaceutical company focused on developing antibody-based therapeutics for the treatment of solid tumor cancer. The Companys product candidates include Mecbotamab vedotin (BA3011), Ozuriftabmab vedotin (BA3021), evalstotug (BA3071), and BA3182. Its lead product candidate, BA3011, is a conditionally active biologics (CAB) antibody-drug conjugate that targets AXL, which is a protein kinase receptor. The BA3071 is a potential therapeutic for multiple solid tumor types, including soft tissue and bone sarcoma, and non-small cell lung cancer. BA3021 is developing a CAB antibody drug conjugate directed against a receptor tyrosine kinase such as orphan receptor 2 (ROR2). BA3071 is a CAB anti-CTLA-4 antibody that is being developed as a therapeutic for multiple solid tumor indications. BA3182 is designed with an EpCAM binding domain and a CD3 binding domain, both binding domains with CAB activity (Dual-CAB), for the treatment of advanced adenocarcinoma.', 'San Diego, CA, United States', 61);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('EXPE', 'STOCK', 'Expedia Group, Inc.', 'NMS', 'Consumer Cyclical', 'Travel Services', 'Expedia Group, Inc. É uma empresa de viagens online. Os segmentos da empresa incluem B2C, B2B e trivago. O segmento B2C oferece uma gama completa de serviços de viagens e publicidade para seus clientes em todo o mundo através de uma variedade de marcas de consumo, incluindo: Expedia.com, Hotels.com, VRBO, Orbitz, Travelocity, Wotif Grupo, Bookers, CheapTickets, Hotwire.com e CarRentals.com. O segmento B2B alimenta uma ampla gama de empresas de viagens e não viagens, incluindo companhias aéreas, agentes de viagens offline, varejistas on-line, gestão de viagens corporativas e instituições financeiras, que aproveitam a tecnologia de viagens e aproveitam sua oferta diversificada para aumentar suas ofertas e comercializar tarifas e disponibilidades do Expedia Group para seus viajantes. Seu segmento trivago gera receita de publicidade principalmente através do envio de referências para empresas de viagens on-line e provedores de serviços de viagens a partir de seus sites de metasearch de hot', 'Seattle, WA, United States', 16500);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('DUOL', 'STOCK', 'Duolingo, Inc.', 'NMS', 'Technology', 'Software - Application', 'Duolingo, Inc. É uma empresa de tecnologia. A empresa está empenhada em oferecer uma plataforma de aprendizagem móvel, bem como um exame digital de avaliação de proficiência em inglês. Ele opera um modelo de negócio freemium, a saber, o aplicativo e o site são acessíveis gratuitamente, embora Duolingo também oferece serviços premium por uma taxa de assinatura. Suas soluções consistem no Duolingo App, Super Duolingo, Duolingo Max, Duolingo English Test: AI-Driven Language Assessment, Duolingo para Escolas, e Duolingo ABC. O Duolingo App oferece cursos em mais de 40 idiomas diferentes, incluindo espanhol, inglês, francês, alemão, italiano, português, japonês e chinês. Duolingo também pode ser acessado em computadores desktop através de um navegador da Web. Sua oferta de assinatura, Super Duolingo, oferece aos alunos recursos adicionais para melhorar sua experiência de aprendizado. O Duolingo English Test é uma avaliação de proficiência em inglês on-line, on-demand e high-stakes. Ele tamb', 'Pittsburgh, PA, United States', 830);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('AQST', 'STOCK', 'Aquestive Therapeutics, Inc.', 'NGM', 'Healthcare', 'Drug Manufacturers - Specialty & Generic', 'Aquestive Therapeutics, Inc. is a pharmaceutical company. The Company is engaged in developing orally administered and topical gel products to deliver complex molecules, providing novel alternatives to invasive and inconvenient standard of care therapies. It has four commercialized products marketed by the Companys licensees in the United States and around the world and is the manufacturer of these licensed products. The Company also collaborates with pharmaceutical companies to bring new molecules to market using proprietary technologies, including PharmFilm, and has proven drug development and commercialization capabilities. The Company is advancing a late-stage proprietary product candidate for the treatment of severe allergic reactions, including anaphylaxis, and an early-stage epinephrine prodrug topical gel product candidate for various possible dermatology conditions. Its portfolio includes Anaphylm, AQST-108, Libervant, Suboxone, and Emylif.', 'Warren, NJ, United States', 142);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('FBL', 'STOCK', 'GraniteShares 2x Long META Daily ETF', 'NGM', NULL, NULL, 'The fund is an actively managed exchange traded fund that attempts to replicate 2 times (200%) the daily percentage change of the underlying stock by entering into financial instruments such as swaps and options on the underlying stock as well as directly purchasing the underlying stock. The fund will aim to primarily obtain its notional exposure against the underlying stock through swap agreements.', '', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CVM', 'STOCK', 'CEL-SCI Corporation', 'ASE', 'Healthcare', 'Biotechnology', 'CEL-SCI Corporation is a late clinical-stage biotechnology company. It is engaged in research and development directed at improving the treatment of cancer and other diseases by using the immune system, the bodys natural defense system. It is focused on the development of product candidates and technologies, including Multikine and Ligand Epitope Antigen Presentation System technology (LEAPS). Multikine (Leukocyte Interleukin, Injection), a true first-line cancer therapy, which is designed to help the immune system target the tumor at a time when the immune system is still relatively intact and thereby thought to be better able to mount an attack on the tumor. LEAPS is designed to stimulate the human immune system to more effectively fight bacterial, viral and parasitic infections as well as autoimmune conditions, allergies, transplantation rejection and cancer, when it cannot do so on its own. The LEAPS candidates include CEL-2000, CEL-4000 and DerG-PG275(Cit) (aka, CEL-5000).', 'Vienna, VA, United States', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BDX', 'STOCK', 'Becton, Dickinson and Company', 'NYQ', 'Healthcare', 'Medical Instruments & Supplies', 'Becton, Dickinson and Company é uma empresa global de tecnologia médica. A empresa está envolvida no desenvolvimento, fabricação e venda de uma ampla gama de suprimentos médicos, dispositivos, equipamentos de laboratório e produtos de diagnóstico usados por instituições de saúde, médicos, pesquisadores de ciências da vida, laboratórios clínicos e muito mais. Seu segmento BD Medical produz uma série de tecnologias e dispositivos médicos que são usados para ajudar a melhorar a prestação de cuidados de saúde em uma variedade de configurações. O segmento BD Medical é composto por várias unidades de negócios, incluindo soluções de entrega de medicamentos, soluções de gestão de medicamentos, monitoramento avançado e sistemas farmacêuticos. Seu segmento BD Life Sciences fornece produtos para a coleta e transporte de amostras de diagnóstico e instrumentos e sistemas de reagentes para detetar uma série de doenças infeciosas, infeções associadas à saúde e cânceres. Seu segmento Intervencional BD', 'Franklin Lakes, NJ, United States', 70000);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('ALGM', 'STOCK', 'Allegro MicroSystems, Inc.', 'NMS', 'Technology', 'Semiconductors', 'Allegro MicroSystems, Inc. is engaged in power and sensing semiconductor solutions for motion control and energy-efficient systems. The Company is a global designer, developer, fabless manufacturer and marketer of sensor integrated circuits (ICs) and application-specific power ICs, enabling the important emerging technologies in the automotive and industrial markets. Its sensor ICs enable its customers to measure motion, speed, position and current, while its power ICs include high-temperature and high-voltage capable motor drivers, power management ICs, light-emitting diode (LED) driver ICs and isolated gate drivers. Its product portfolio includes over 1,500 products across a range of high-performance analog mixed-signal semiconductors. The Company also offers tunneling magnetoresistance (TMR) sensor solutions that provide the highest magnetic sensitivity, lowest power consumption, and the smallest size by comparison to other magnetic technologies.', 'Manchester, NH, United States', 4060);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('DHC', 'STOCK', 'Diversified Healthcare Trust', 'NMS', 'Real Estate', 'REIT - Healthcare Facilities', 'Diversified Healthcare Trust is a real estate investment trust (REIT). The Company is focused on owning healthcare properties located throughout the United States. It has an approximately $7.2 billion portfolio, which includes over 367 properties in 36 states and Washington, D.C., occupied by approximately 450 tenants, and totaling approximately 8.0 million square feet of life science and medical office properties and more than 27,000 senior living units. The Company''s segments include Medical Office and Life Science Portfolio and SHOP. Its Medical Office and Life Science Portfolio segment primarily consists of medical office properties leased to medical providers and other medical-related businesses, as well as life science properties primarily leased to biotech laboratories and other similar tenants. Its SHOP segment consists of managed senior living communities that provide short-term and long-term residential living and, in some instances, care and other services for residents.', 'Newton, MA, United States', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('AIP', 'STOCK', 'Arteris, Inc.', 'NGM', 'Technology', 'Semiconductors', 'Arteris, Inc. is a provider of semiconductor system IP, including interconnect and other intellectual property, (collectively, System IP) technology. Its System IP technology manages the on-chip communications and IP block deployments in System-on-Chip (SoC) semiconductors and systems of chiplets. It provides semiconductor interconnect IP and SoC Integration Automation software solutions to serve its target end-markets, including automotive, enterprise computing, consumer electronics, communications across wired and wireless, and industrial markets. Its two core product platforms are Network-on-Chip IP Products and SoC Integration Automation Software Solutions Products. Its Network-on-Chip IP products include Non-coherent NoC IP, with FlexGen, FlexNoC and FlexWay; Cache-coherent NoC IP, with Ncore, and NoC interface IP, with CodaCache. Its SoC Integration Automation Software Solutions products include IP and SoC software, with Magillem (Connectivity and Registers) and CSRCompiler.', 'Campbell, CA, United States', 278);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('ALBT', 'STOCK', 'Avalon GloboCare Corp.', 'NCM', 'Real Estate', 'Real Estate Services', 'Avalon GloboCare Corp. is a commercial-stage company engaged in developing and delivering precision diagnostic consumer products and the advancement of intellectual property in cellular therapy. The Company operates in two reportable business segments: the real property operating segment and the laboratory testing services segment. Its laboratory services offer a test menu that satisfies the clients'' testing needs, from general bloodwork to anatomic pathology, urine toxicology, pharmacogenomics (PGx) testing and more, and it is a one-stop-shop for clinical testing. The Company is currently marketing the KetoAir breathalyzer device and plans to develop additional diagnostic uses of the breathalyzer technology. The KetoAir breathalyzer is a handheld device that allows the user to detect acetone levels in exhaled breath. The acetone level is in concentration units part-per-million (ppm) such that the user knows the real-time ketosis status.', 'Freehold, NJ, United States', 4);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CPRX', 'STOCK', 'Catalyst Pharmaceuticals, Inc.', 'NCM', 'Healthcare', 'Biotechnology', 'Catalyst Pharmaceuticals, Inc. is a commercial-stage biopharmaceutical company focused on in-licensing, developing, and commercializing medicines for patients living with rare and difficult-to-treat diseases. The Company markets three drug products: FIRDAPSE (amifampridine), FYCOMPA (perampanel), and AGAMREE (vamorolone). FIRDAPSE is a treatment for people six years of age or older living with Lambert-Eaton myasthenic syndrome (LEMS). The Company, through its sub-licensee, also offers FIRDAPSE in Canada and Japan. FYCOMPA is a prescription medicine used alone or with other medicines to treat partial-onset seizures with or without secondarily generalized seizures in people with epilepsy aged four and older and with other medicines to treat primary generalized tonic-clonic seizures in people with epilepsy aged 12 and older. AGAMREE is a corticosteroid with a structure that retains the potent anti-inflammatory effects of traditional corticosteroids used in Duchenne muscular dystrophy.', 'Coral Gables, FL, United States', 181);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CENN', 'STOCK', 'Cenntro Inc.', 'NCM', 'Consumer Cyclical', 'Auto Manufacturers', 'Cenntro Inc. is a holding company, which conducts operations through its subsidiaries in the United States, Australia, Europe, Mexico, Hong Kong, the Dominican Republic, and in the Peoples Republic of China. The Company is engaged in designing and manufacturing purpose-built, electric commercial vehicles (ECVs) used primarily in last mile delivery and industrial applications. Through its distribution partners, it provides robust pre-market and after-sales services, and support for its customers. Its vehicles include Logistar 210, Logistar 250, Logistar 260, Logistar 300, Logistar 400, Logistar 450, AVANTIER, Metro, and TeeMak. The Company''s subsidiaries include Cenntro Electric Group Pty Limited, Cenntro Electric Group, Inc., Cenntro Electric Group (Europe) GmbH, Teemak Power Corporation, among others.', 'Freehold, NJ, United States', 260);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('EGO', 'STOCK', 'Eldorado Gold Corporation', 'NYQ', 'Basic Materials', 'Gold', 'Eldorado Gold Corporation is a Canada-based gold and base metals producer with mining, development and exploration operations in Turkiye, Canada and Greece. The Company operates four mines: Kisladag, Efemcukuru, Lamaque, and Olympias. The Kisladag, Efemcukuru and Lamaque mines are gold mines, while Olympias is a polymetallic operation producing three concentrates bearing gold, lead-silver and zinc. Kisladag is a low-grade, bulk-tonnage, open-pit operation that uses heap leaching for gold recovery. It is located in Usak Province in western Turkiye. Efemcukuru is a high-grade, underground operation located in Izmir Province in western Turkiye. The Lamaque Complex is located in Val-d''Or, Quebec. It includes the Triangle mine, the Ormaque mine, the Parallel deposit, the Plug #4 deposit, and the Sigma Mill. Olympias is a gold-silver-lead-zinc mine located in the Halkidiki Peninsula in northern Greece. It also operates the Bruell Gold Project 51 claim property in Vauquelin township, Quebec.', 'Vancouver, BC, Canada', 5800);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('ABCB', 'STOCK', 'Ameris Bancorp', 'NYQ', 'Financial Services', 'Banks - Regional', 'Ameris Bancorp is a financial holding company, through its banking subsidiary, Ameris Bank (the Bank), which provides a full range of banking services to its retail and commercial customers, who are primarily concentrated in select markets in Georgia, Alabama, Florida, North Carolina and South Carolina. Its Banking Division provides full-service financial services to include commercial loans, consumer loans and deposit accounts. Its Retail Mortgage Division is engaged in the origination, sales and servicing of one-to-four family residential mortgage loans. Its Warehouse Lending Division is engaged in the origination and servicing of warehouse lines to other businesses that are secured by underlying one-to-four family residential mortgage loans and residential mortgage servicing rights. Its Premium Finance Division is engaged in the origination and servicing of commercial insurance premium finance loans. It operates 164 full-service domestic banking offices.', 'Atlanta, GA, United States', 2672);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('ENOV', 'STOCK', 'Enovis Corporation', 'NYQ', 'Healthcare', 'Medical Devices', 'A Enovis Corporation é uma empresa de tecnologia médica. Está focada no desenvolvimento de soluções clinicamente diferenciadas através da fabricação e distribuição de dispositivos médicos com uma gama de produtos utilizados para cirurgia reconstrutiva, reabilitação, gestão da dor e fisioterapia. Atua através de dois segmentos: Prevenção e Recuperação e Reconstrutiva. O segmento de Prevenção e Recuperação desenvolve, fabrica e distribui produtos rígidos de apoio, produtos ortopédicos, sistemas vasculares e vestuário de compressão, produtos de terapia a quente e fria e oferece produtos de ciências de recuperação nos mercados de reabilitação clínica e medicina esportiva, como estimuladores de crescimento ósseo e estimuladores elétricos usados para o gerenciamento da dor. O segmento Reconstructive desenvolve, fabrica e comercializa uma variedade de joelho, quadril, ombro, cotovelo, pé, produtos de implante de tornozelo e dedo e ferramentas de produtividade cirúrgica. Ele também fornece Nov', 'Wilmington, DE, United States', 7367);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CYCU', 'STOCK', 'Cycurion, Inc.', 'NGM', 'Technology', 'Information Technology Services', 'Cycurion, Inc. is a technology-enabled cybersecurity company. The Company provides network communications and information technology security solutions. It also provides proprietary solutions to federal, state and local government agencies and commercial partners. Its ARx platform is a turnkey Web application protection and managed security solution that combines all the essential cybersecurity layers in a comprehensive platform. It provides comprehensive managed information technology (IT) services to help organizations optimize their technology infrastructure and operations. Its security management solution is designed to help organizations protect their digital assets against various cyber threats. Its Managed Security Service includes 24/7 monitoring, threat detection, incident response, and remediation. Its Consulting and Advisory Services include security control assessments, staff augmentation, digital modernization, security architecture and engineering, and others.', 'McLean, VA, United States', 46);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CINF', 'STOCK', 'Cincinnati Financial Corporation', 'NMS', 'Financial Services', 'Insurance - Property & Casualty', 'A Cincinnati Financial Corporation oferece principalmente seguros de negócios, de casa e de automóveis, seu principal negócio, através da Cincinnati Insurance Company e suas duas empresas padrão de acidentes de propriedade no mercado. Seus segmentos incluem seguro de linhas comerciais, seguro de linhas pessoais, seguro de excesso e excesso de linhas, seguro de vida e investimentos. O negócio do segmento de seguros de linhas comerciais inclui acidentes comerciais, propriedade comercial, automóvel comercial, compensação de trabalhadores e outras linhas comerciais. O negócio do segmento de Seguros inclui auto pessoal, proprietário e outras linhas pessoais. O segmento de Seguros de Excesso e Excedente abrange a vítima comercial e a propriedade comercial. O negócio do segmento de seguro de vida inclui seguro de vida a termo, produtos de local de trabalho, seguro de vida inteiro e seguro de vida universal. O segmento de investimentos investe o dinheiro que gera a partir de operações de segur', 'Fairfield, OH, United States', 5624);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CGON', 'STOCK', 'CG Oncology, Inc.', 'NMS', 'Healthcare', 'Biotechnology', 'CG Oncology, Inc. is a late-stage clinical biopharmaceutical company focused on developing and commercializing a potential backbone bladder-sparing therapeutic for patients afflicted with bladder cancer. The Companys product candidate, cretostimogene grenadenorepvec (cretostimogene) is in clinical development for the treatment of patients with non-muscle invasive bladder cancer (NMIBC). Its BOND-003 is in Phase III clinical trial, which is designed to assess the safety and efficacy of cretostimogene in high-risk Bacillus Calmette Guerin (BCG)-unresponsive NMIBC when administered as a monotherapy. Its CORE-001 is a Phase II single-arm, open-label clinical trial of cretostimogene administered with BCG-unresponsive NMIBC. Its portfolio also includes PIVOT-006, a Phase III trial to assess the safety and efficacy of adjuvant cretostimogene when administered as monotherapy to patients with intermediate-risk NMIBC following transurethral resection of the bladder tumor (TURBT).', 'Irvine, CA, United States', 113);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BWXT', 'STOCK', 'BWX Technologies, Inc.', 'NYQ', 'Industrials', 'Aerospace & Defense', 'BWX Technologies, Inc. is a specialty manufacturer of nuclear components, a developer of nuclear technologies and a service provider. The Company also provides special nuclear materials processing, environmental site restoration services, products, and services. Its Government Operations segment manufactures naval nuclear reactors, including related nuclear fuel, for the United States Naval Nuclear Propulsion Program for use in submarines and aircraft carriers. It also fabricates fuel-bearing precision components that range in weight from a few grams to hundreds of tons, manufactures electromechanical equipment, and performs design, manufacturing, inspection, assembly, and testing activities. The Company is also a provider of advanced specialty materials, fabrication, and high-strength alloy manufacturing. Its Commercial Operations segment designs and manufactures commercial nuclear steam generators, heat exchangers, pressure vessels, reactor components, and other auxiliary equipment.', 'Lynchburg, VA, United States', 8700);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BSY', 'STOCK', 'Bentley Systems, Incorporated', 'NMS', 'Technology', 'Software - Application', 'Bentley Systems, Inc is an infrastructure engineering software company. It focuses on developing and supporting a portfolio of integrated software offerings across professional disciplines, infrastructure sectors, geographies, and the infrastructure lifecycle. It serves enterprises and professionals across the infrastructure lifecycle by enabling project delivery and asset performance. Its portfolio of integrated software offerings includes engineering applications, geo-professional applications, Bentley Infrastructure Cloud, Bentley Asset Analytics, and Cesium and iTwin Platform. Its geo-professional applications include GeoStudio, for integrated geotechnical analysis of slope stability, groundwater flow, and heat and mass transfer in soil and rock, and Imago, for the capture and management of drilling core images. Bentley Asset Analytics solutions include Blyncsy, for inspecting and detecting roadway conditions, and Bridge Monitoring, for identifying and classifying bridge defects.', 'Exton, PA, United States', 5500);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CXM', 'STOCK', 'Sprinklr, Inc.', 'NYQ', 'Technology', 'Software - Application', 'Sprinklr, Inc. is an enterprise software company for customer-facing functions. The Company''s artificial intelligence (AI) platform, Unified Customer Experience Management (Unified-CXM), enables customer-facing teams, from customer service to marketing, to collaborate across internal silos, communicate across digital channels, and leverage AI to deliver better customer experience. Its platform enables organizations to connect to approximately 30 digital channels, including messaging, live chat, text, social media and hundreds of millions of forums, blogs, news and review sites, as well as traditional channels such as voice and email. Its single-codebase platform is designed to handle unstructured data. Its Unified-CXM platform enables listening, seamless collaboration across the entire customer journey, skills-based workflow, customer-led governance, and timely decision-making.', 'New York, NY, United States', 3589);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CRON', 'STOCK', 'Cronos Group Inc.', 'NGM', 'Healthcare', 'Drug Manufacturers - Specialty & Generic', 'Cronos Group Inc. is a global cannabinoid company engaged in building disruptive intellectual property by advancing cannabis research, technology and product development. The Companys diverse international brand portfolio includes Spinach, PEACE NATURALS and Lord Jones. Spinach is a mainstream adult-use cannabis brand with a portfolio that includes cannabinoid products in a wide range of formats, including dried flower, pre-rolls, vaporizers, edibles and tinctures. Lord Jones is a premium adult-use cannabis brand. The Lord Jones brand portfolio includes cannabis products in the pre-roll, vaporizer and edible categories. Lord Jones Ice Water Hash Fusions pre-rolls feature flower and terpene-rich ice water hash and are fitted with a branded ceramic tip. PEACE NATURALS is a global medical brand engaged in producing cannabis products. The Company distributes products under the PEACE NATURALS brand for the Canadian market and the Israeli, German, and United Kingdom medical markets.', 'Stayner, ON, Canada', 459);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CDXS', 'STOCK', 'Codexis, Inc.', 'NMS', 'Healthcare', 'Biotechnology', 'Codexis, Inc. is a provider of enzymatic solutions. It is leveraging its CodeEvolver technology platform to discover, develop, enhance, and commercialize novel, high-performance enzymes and other classes of proteins. The Company utilizes its CodeEvolver technology platform to develop optimized enzymes that are used by some pharmaceutical companies to reduce their costs and improve the efficiency and productivity of their manufacturing processes for small molecule therapeutics. It is developing its Enzyme-Catalyzed Oligonucleotide Synthesis (ECO Synthesis) manufacturing platform to enable the scaled manufacture of RNA interference (RNAi) and other RNA-based therapeutics through an enzymatic route. The Company also uses the CodeEvolver platform technology to develop enzymes for the synthesis of RNAi therapeutics through its ECO Synthesis manufacturing platform, where its enzymes are poised to deliver many of the same benefits it offers in pharmaceutical manufacturing.', 'Redwood City, CA, United States', 188);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('EVTV', 'STOCK', 'Envirotech Vehicles, Inc.', 'NCM', 'Consumer Cyclical', 'Auto Manufacturers', 'Envirotech Vehicles, Inc. is a provider of purpose-built zero-emission electric vehicles focused on reducing the total cost of vehicle ownership and helping fleet operators unlock the benefits of green technology. The Company serves commercial and last-mile fleets, school districts, public and private transportation service companies and colleges and universities. Its vehicles address the challenges of traditional fuel price cost instability and local, state and federal environmental regulatory compliance. It offers Class 2 through 4 logistics vans, Class 4 through 5 urban trucks, school buses, electric forklifts, street sweepers, neighborhood electric vehicles and right-hand drive vans and urban trucks. Its vehicles include options for telemetric for remote monitoring, electric power-export and various levels of grid-connectivity. The Companys vehicles are manufactured by original equipment manufacturers (OEM) located in China, Malaysia and the Philippines.', 'Houston, TX, United States', 22);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CDP', 'STOCK', 'COPT Defense Properties', 'NYQ', 'Real Estate', 'REIT - Office', 'A COPT Defense Properties é uma confiança de investimento imobiliário totalmente integrada e autogestionada (REIT). Está focado em possuir, operar e desenvolver propriedades em locais próximos ou, por vezes, conter instalações e missões de defesa do Governo dos Estados Unidos (USG) (referido como seu Portfólio de Defesa/TI). Seus inquilinos incluem o USG e seus empreiteiros de defesa, que estão envolvidos em atividades prioritárias de segurança nacional, e que exigem melhorias de propriedade de alta segurança e missão crítica. Seu segmento de Portfólio de Defesa/TI é composto por aproximadamente 193 propriedades, incluindo 24 de propriedade de joint ventures não consolidadas, abrangendo 22,0 milhões de pés quadrados. Seus subsegmentos de portfólio de Defesa/TI incluem Fort George G. Meade e o Corredor de Baltimore/Washington; locais de Defesa/TI da Virgínia do Norte; Base da Força Aérea Lackland em San Antonio, Texas; locais que servem a Marinha dos EUA; Arsenal Redstone em Huntsville,', 'Columbia, MD, United States', 427);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CANG', 'STOCK', 'Cango Inc.', 'NYQ', 'Financial Services', 'Capital Markets', 'Cango Inc is a holding company principally engaged in the provision of automotive transaction service platform. The Company mainly provides automobile trading solutions and after-market services facilitation services. Automobile trading solutions mainly include car sourcing and transaction facilitation services, along with logistics and warehousing support for dealers, which are accessible primarily through Cango U-car application (app). After-market services facilitation services include the sale of insurance policies and other after-market services. The Company provides automotive financing facilitation services, as well as value-added services, such as assistance with administrative procedures associated with car purchasing and financing. The Company also operates overseas crypto mining business.', 'Shanghai, , China', 217);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('HCC', 'STOCK', 'Warrior Met Coal, Inc.', 'NYQ', 'Basic Materials', 'Coking Coal', 'Warrior Met Coal, Inc. is a producer and exporter of steelmaking coal, also known as hard coking coal (HCC), operating longwall operations in its underground mines based in Alabama. The Companys two operating mines, Mine No. 4 and Mine No. 7, and Blue Creek are located approximately 300 miles from its export terminal at the Port of Mobile in Alabama. The Company sells its coal to a diversified customer base of blast furnace steel producers, primarily located in Europe, South America and Asia. The Companys HCC, mined from the Southern Appalachian region of the United States, is characterized by low-to-high volatile matter (VM) and high coke strength after reaction (CSR). Mine No.7 operates two longwalls, while Mine No.4 runs a single longwall. Mine No. 4 and Mine No. 7 are located approximately 20 miles east of Tuscaloosa, Alabama and 30 miles southwest of Birmingham, Alabama.', 'Brookwood, AL, United States', 1336);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CGAU', 'STOCK', 'Centerra Gold Inc.', 'NYQ', 'Basic Materials', 'Gold', 'Centerra Gold Inc. is a Canada-based gold mining company focused on operating, developing, exploring and acquiring gold and copper properties in North America, Turkiye, and other markets worldwide. Centerra operates two mines: the Mount Milligan Mine in British Columbia, Canada, and the Oksut Mine in Turkiye. The Company also owns the Kemess Project in British Columbia, Canada, the Goldfield Project in Nevada, United States, and owns and operates the Molybdenum Business Unit in the United States and Canada. The Mount Milligan Mine is an open pit mine located in north central British Columbia, Canada producing a gold and copper concentrate. The Oksut Mine is located approximately 300 kilometers southeast of Ankara in Turkiye and 48 kilometers south of Kayseri, the provincial capital. The Molybdenum BU includes the Langeloth Facility in Pennsylvania and two North American molybdenum mines: the Thompson Creek Mine in Idaho and the 75%-owned Endako Mine in British Columbia.', 'Toronto, ON, Canada', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CAR', 'STOCK', 'Avis Budget Group, Inc.', 'NMS', 'Industrials', 'Rental & Leasing Services', 'Avis Budget Group, Inc. is a provider of mobility solutions through its three brands, Avis, Budget and Zipcar, as well as several other brands. Its brands offer a range of options, from car and truck rental to car sharing. Its segments include Americas and International. The Americas segment consists primarily of vehicle rental operations in North America, South America, Central America and the Caribbean and operates car-sharing operations in certain of these markets. The International segment consists of vehicle rental operations in Europe, the Middle East, Africa, Asia and Australasia, and operates car-sharing operations in certain of these markets. The Avis brand provides vehicle rental and other mobility solutions at price points generally above non-branded and value-branded vehicle rental companies and serves the premium commercial and leisure segments of the travel industry. Its other brands include Payless, Apex, AmicoBlu, Maggiore, Morini Rent, FranceCars, and others.', 'Parsippany, NJ, United States', 17000);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('ACMR', 'STOCK', 'ACM Research, Inc.', 'NGM', 'Technology', 'Semiconductor Equipment & Materials', 'ACM Research, Inc. develops, manufactures and sells semiconductor process equipment spanning cleaning, electroplating, stress-free polishing, vertical furnace processes, track, plasma-enhanced chemical vapor deposition (PECVD), and wafer- and panel-level packaging tools, enabling advanced and semi-critical semiconductor device manufacturing. It has also developed Timely Energized Bubble Oscillation (TEBO) technology for application in wet wafer cleaning during the fabrication of two-dimensional (2D) and three-dimensional wafers with fine feature sizes. It has designed these tools for use in fabricating foundry, logic, and memory chips, including dynamic random-access memory (DRAM), 3D NAND-flash memory chips, and compound semiconductor chips. It also develops, manufactures, and sells a range of advanced packaging tools to wafer assembly and packaging customers. Its other advanced packaging tools include Ultra ECP ap, Ultra C Developer, and Ultra C PR Megasonic-Assisted Stripper.', 'Fremont, CA, United States', 2023);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('ELVA', 'STOCK', 'Electrovaya Inc.', 'NCM', 'Industrials', 'Electrical Equipment & Parts', 'Electrovaya Inc. is a Canada-based lithium-ion battery technology and manufacturing company. The Company designs, develops and manufactures proprietary lithium-ion batteries, battery systems, and battery-related products for energy storage, clean electric transportation, and other specialized applications. The Company is focused on contributing to the prevention of climate change by supplying safe and long-lasting lithium-ion batteries. Its technology includes infinity battery technology and solid state battery technology. The Companys product portfolio includes cell technology, infinity battery systems, low voltage, and high voltage. Its infinity battery system-high voltage applications include E-buses, E-trucks, Mining Vehicles, and Defense. The Company has two operating sites in Canada and a 52-acre site with a 135,000 square foot manufacturing facility in Jamestown, New York state for its planned gigafactory.', 'Mississauga, ON, Canada', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BWEN', 'STOCK', 'Broadwind, Inc.', 'NCM', 'Industrials', 'Specialty Industrial Machinery', 'Broadwind Inc. is a precision manufacturer of structures, equipment and components for clean tech and other specialized applications. The Companys segments include Heavy Fabrications, Gearing, and Industrial Solutions. The Heavy Fabrications segment provides large, complex and precision fabrications to customers in a broad range of industrial markets. The Gearing segment provides gearing, gearboxes and precision machined components to a set of customers in diverse markets, including surface and underground mining, wind energy, steel, material handling, infrastructure, onshore and offshore oil and gas fracking and drilling, marine, defense, and other industrial markets. It also provides gearbox repair services. Industrial Solutions provides supply chain solutions, light fabrication, inventory management, kitting, and assembly services, primarily serving the combined cycle natural gas turbine market. It also provides packaging solutions and fabricates panels and sub-assemblies.', 'Cicero, IL, United States', 411);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CNM', 'STOCK', 'Core & Main, Inc.', 'NYQ', 'Industrials', 'Industrial Distribution', 'Core & Main, Inc. is a holding company. The Company is advancing reliable infrastructure with local service, nationwide. The Company is a specialized distributor with a focus on water, wastewater, storm drainage and fire protection products, and related services. It provides solutions to municipalities, private water companies and professional contractors across municipal, non-residential, and residential end markets, nationwide. With approximately 370 locations across the United States, the Company provides its customers local expertise backed by a national supply chain. The Companys offering consists of pipes, valves and fittings, storm drainage products, fire protection products and smart metering products. The Company is also a provider of geosynthetics and erosion control products. It distributes a range of storm drainage products, with custom fabrication capabilities.', 'Saint Louis, MO, United States', 5700);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('AKAM', 'STOCK', 'Akamai Technologies, Inc.', 'NMS', 'Technology', 'Software - Infrastructure', 'Akamai Technologies, Inc. É uma empresa de segurança cibernética e computação em nuvem. A empresa desenvolve e fornece soluções para que as empresas globais criem, protejam e aceleram suas aplicações e experiências digitais. A Akamai Connected Cloud, a plataforma da empresa, é composta por uma arquitetura de borda e nuvem e uma rede subjacente para serviços de computação em nuvem, segurança e entrega de conteúdo. Seus serviços de computação em nuvem incluem serviços de computação, armazenamento, rede, banco de dados e gerenciamento de contentores necessários para criar, implantar e proteger aplicativos e cargas de trabalho. Suas soluções de segurança são projetadas para manter a infraestrutura, sites, aplicativos, interfaces de programação de aplicativos e usuários seguros de uma infinidade de ataques cibernéticos e ameaças on-line, melhorando o desempenho. Suas soluções de entrega de conteúdo consistem em soluções focadas em desempenho na Web e em dispositivos móveis e soluções de ent', 'Cambridge, MA, United States', 10944);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('AGH', 'STOCK', 'Aureus Greenway Holdings Inc.', 'NCM', 'Consumer Cyclical', 'Leisure', 'Aureus Greenway Holdings Inc. is a holding company. The Company conducts business activities principally through its wholly owned subsidiaries, Chrome Fields I, Inc. and Chrome Fields II, Inc., which are engaged in the operation of golf courses and selling of merchandise and food and beverages, respectively. The Company owns and operates two public golf country clubs in Florida that each features a golf-club, consisting of over 289 acres of multiservice recreational property. The Companys golf country clubs include two golf-courses with over 13,000 yards of combined fairways, clubhouses boasting food and beverage options, aquatic golf ranges, and pro shops to assist any level of golfer. Each of its golf country clubs is organized into four principal business sectors: golf recreation, retail golf products, and equipment and facilities rental; membership dues; food and beverage services, and ancillary services and amenities.', 'Kissimmee, FL, United States', 47);\n
