INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('OOSB', 'STOCK', 'One+One S&P 500 and Bitcoin ETF', 'NGM', NULL, NULL, 'Under normal circumstances, the fund manager expects to rebalance its portfolio back to 100% exposure to the S&P 500 and 100% exposure to Bitcoin after the difference in asset weights exceeds 20% (i.e., 110% + 90%) on two consecutive market closes, or after the sum of the assets weights falls below 90% or exceeds 110% of the sum of the target exposures on two consecutive market closes. The fund is non-diversified.', '', NULL);\n
