INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('DIVD', 'STOCK', 'Altrius Global Dividend ETF', 'NGM', NULL, NULL, 'To pursue its objective, the fund will invest at least 90% of its net assets, plus the amount of any borrowings for investment purposes, in dividend-paying equity securities. The principal type of equity security in which the fund will invest is common stock. Under normal market conditions, at least 30% of the fund’s assets will be invested outside the United States.', '', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('EICA', 'STOCK', 'Eagle Point Income Company Inc.', 'NYQ', 'Financial Services', 'Asset Management', 'Eagle Point Income Company Inc. is an externally managed, diversified closed-end management investment company. The Companys primary investment objective is to generate high current income, with a secondary objective to generate capital appreciation. The Company seeks to achieve these objectives by investing primarily in junior debt tranches of collateralized loan obligations (CLOs) rated BB. In addition, the Company may invest up to 35% of its total assets (at the time of investment) in CLO equity securities and other securities and instruments that are consistent with its investment objectives. The Company may also invest in other securities and instruments objectives such as securities issued by other securitization vehicles. The Company may also invest in other junior debt tranches of CLOs, senior debt tranches of CLOs, loan accumulation facilities (LAF) and other related securities and instruments. The investment advisor of the Company is Eagle Point Income Management LLC.', 'Greenwich, CT, United States', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BULD', 'STOCK', 'Pacer BlueStar Engineering the Future ETF', 'NGM', NULL, NULL, 'The index is a rules-based index that consists of globally-listed stocks and depositary receipts of companies that derive at least 50% of their revenues from Robotics and 3D Printing Companies, as determined by the index provider. The fund adviser will generally use a “replication” strategy to achieve its investment objective, meaning it will invest in all of the component securities of the index. The fund is non-diversified.', '', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('DMAA', 'STOCK', 'Drugs Made In America Acquisition Corp.', 'NGM', 'Financial Services', 'Shell Companies', 'Drugs Made In America Acquisition Corp. is a blank check company. The Company is incorporated as an exempted company for the purpose of effecting a merger, share exchange, asset acquisition, share purchase, recapitalization, reorganization or other similar business combination with one or more businesses. Its efforts to identify a prospective target business will not be limited to a particular industry or geographic region although it intends to focus its search for business combination targets in the pharmaceutical industry.', 'Fort Lauderdale, FL, United States', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BOTJ', 'STOCK', 'Bank of the James Financial Group, Inc.', 'NCM', 'Financial Services', 'Banks - Regional', 'Bank of the James (the Bank) is a full-service commercial and retail bank. The Bank is a wholly owned subsidiary of Bank of the James Financial Group, Inc. The Bank provides mortgage loan origination through Bank of the James Mortgage, a division of Bank of the James. The Bank offers full investment and insurance services through its BOTJ Investment Services division and BOTJ Insurance, Inc. subsidiary. The Bank offers a full range of short- to medium-term commercial and consumer loans. The Bank services customers in Virginia from offices located in Altavista, Amherst, Appomattox, Bedford, Blacksburg, Buchanan, Charlottesville, Forest, Harrisonburg, Lexington, Lynchburg, Madison Heights, Nellysford, Roanoke, Rustburg, and Wytheville.', 'Lynchburg, VA, United States', 162);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CPHC', 'STOCK', 'Canterbury Park Holding Corporation', 'NGM', 'Consumer Cyclical', 'Resorts & Casinos', 'Canterbury Park Holding Corporation is the holding company for and parent company of two subsidiaries, Canterbury Park Entertainment LLC (Canterbury Entertainment) and Canterbury Development (Canterbury Development) and Canterbury Park Concessions, Inc. The Companys segments include horse racing, Casino, food and beverage, and real estate development. The horse racing segment represents its pari-mutuel wagering operations on simulcast and live horse races. The Casino segment represents its unbanked card operations. The food and beverage segment includes concessions, catering and events and services provided at the Racetrack. The real estate development segment represents its real estate development operations. The Company conducts its horse racing, Casino, and food and beverage segments through Canterbury Entertainment. The Company conducts its real estate development segment through Canterbury Development.', 'Shakopee, MN, United States', 226);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('AFBI', 'STOCK', 'Affinity Bancshares, Inc.', 'NCM', 'Financial Services', 'Banks - Regional', 'Affinity Bancshares, Inc. is the holding company for Affinity Bank (the Bank). The Banks business consists primarily of taking deposits from the general public and investing those deposits, together with funds generated from operations, in commercial real estate loans, commercial and industrial loans and residential real estate loans and, to a lesser extent, construction and land loans and consumer loans. It makes commercial and industrial loans, primarily in its market area, to a variety of professionals, sole proprietorships and small businesses, including dental loans, which are originated throughout the Southeastern United States. It offers a variety of deposit accounts, including checking accounts, savings accounts and certificate of deposit accounts. It gathers deposits nationwide through its virtual bank, FitnessBank, which accepts deposits and provides higher interest rates based on customers meeting certain fitness goals. It offers a limited range of consumer loans.', 'Covington, GA, United States', 88);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('EBI', 'STOCK', 'Longview Advantage ETF', 'NGM', NULL, NULL, 'The fund invests primarily in equity securities of a diverse group of U.S. companies across market sectors, market capitalizations, and industry groups. Equity securities in which the fund may invest include common stock and derivative instruments that give exposure to equities, such as futures contracts, including futures contracts of U.S. indices.', '', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BRFH', 'STOCK', 'Barfresh Food Group, Inc.', 'NCM', 'Consumer Defensive', 'Beverages - Non-Alcoholic', 'Barfresh Food Group Inc. is engaged in the manufacturing and distribution of ready-to-drink and ready-to-blend frozen beverages. Its portfolio of products includes smoothies, shakes and frappes. Its ready-to-drink smoothie, Twist & Go, is focused on the USDA national school meal program, including the school breakfast program, the national school lunch program and smart snacks in schools program. This sweet fruit and creamy yogurt smoothie contains four ounces of yogurt and a half-cup of fruit/fruit juice and comes in three different flavors: strawberry banana, peach and mango pineapple. Its bulk Easy Pour format, which contains all the ingredients necessary to make the beverage. Its single-serve format features portion-controlled and ready-to-blend beverage ingredient packs or beverage packs. The beverage packs contain all the ingredients necessary to make the beverage, including the base, real fruit pieces, juices and ice-five ounces of water are added before blending.', 'Los Angeles, CA, United States', 11);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BDL', 'STOCK', 'Flanigan''s Enterprises, Inc.', 'ASE', 'Consumer Cyclical', 'Restaurants', 'Flanigan''s Enterprises, Inc. owns and operates a chain of full-service restaurants and package liquor stores. The Company operates in two segments: package stores and restaurants. The operation of package stores consists of retail liquor sales and related items. The operation of restaurants consists of restaurant food and bar sales. The Company operates over 32 units, consisting of restaurants, package liquor stores, a combination restaurant/package liquor stores, and a sports bar, which the Company either owns or has operational control over and has partial ownership; and franchises an additional five units, consisting of two restaurants, one of which it operates and three combination restaurant/package liquor stores. The Company operates its package liquor stores & restaurants under the service marks: Big Daddy''s Liquors, Big Daddys Wine & Liquors, Flanigans Seafood Bar and Grill, and Flanigans. The Company also operates its sports bar under the service mark, Brendans Sports Pu', 'Fort Lauderdale, FL, United States', 665);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('BRID', 'STOCK', 'Bridgford Foods Corporation', 'NGM', 'Consumer Defensive', 'Packaged Foods', 'Bridgford Foods Corporation is primarily engaged in the manufacturing, marketing, and distribution of an extensive line of frozen and snack food products throughout the United States. The Company''s segments include Frozen Food Products and Snack Food Products. The Frozen Food Products segment is engaged in the processing and distribution of frozen food products. The Snack Food Products segment is engaged in the processing and distribution of meat and other convenience foods. It manufactures nearly all of its food products and distribute an extensive line of biscuits, bread dough items, roll dough items, dry sausage products and beef jerky. Its direct store delivery network consists of non-refrigerated snack food products. Its frozen food products division serves both food service and retail customers. It sells approximately 130 frozen food products through wholesalers, cooperatives, and distributors. The Companys snack food products division sells approximately 170 different items.', 'Dallas, TX, United States', 648);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('CCIRU', 'STOCK', 'Cohen Circle Acquisition Corp. I', 'NGM', 'Financial Services', 'Shell Companies', 'Cohen Circle Acquisition Corp. I does not have significant operations. It focuses on effecting a merger, share exchange, asset acquisition, share purchase, reorganization, or other similar business combination, involving one or more businesses or assets. The company was formerly known as FTAC Artemis Acquisition Corp. Cohen Circle Acquisition Corp. I was incorporated in 2021 and is based in Philadelphia, Pennsylvania.', 'Philadelphia, PA, United States', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('AGMI', 'STOCK', 'Themes Silver Miners ETF', 'NGM', NULL, NULL, 'The index is designed to provide exposure to global companies in the large-, mid- and small- capitalization segments that have significant exposure to the silver mining industry. The fund will invest, under normal circumstances, at least 80% of its net assets, plus the amount of any borrowings for investment purposes, in the securities that comprise the Index and in ADRs and GDRs based on the securities in the index. The fund is non-diversified.', '', NULL);\n
INSERT INTO assets_master (ticker, asset_type, name, exchange, sector, industry, business_description, headquarters, employees_count) VALUES ('DUKH', 'STOCK', 'Ocean Park High Income ETF', 'NGM', NULL, NULL, 'The fund is an actively managed exchange-traded fund (“ETF”) that seeks to achieve its investment objective by tactically allocating the fund’s assets between unaffiliated higher yielding ETFs (“High Income ETFs”), long-term Treasury ETFs (“Treasury ETFs”) and cash equivalents. Under normal market conditions, the advisor expects to invest substantially all of its assets in high income ETFs.', '', NULL);\n
